import React, { Component } from 'react';
import './Modal.css';


class Modal extends Component {
    constructor (props) {
        super (props);
    };
        
    
    render() {
        const showModal = this.props.modalVisible;
        const modalErrorFlag = this.props.modalErrorFlag;
        
        if(!showModal) {
            return (
                null
            )
        }
    
        if(showModal && modalErrorFlag) {
            return (
               <div className='showModal'>
                   <h6 className='errorMessage'>You must be at least 21 years old!</h6> 
                   <h3>Are You 21 or Older?</h3>
                   <button className='yes' onClick={this.yesClickHandler}>YES</button>
                   <button className='no' onClick={this.noClickHandler}>NO</button>
                </div>  
            );
        }

        if(showModal) {
            return (
               <div className='showModal'>
                   <h3>Are You 21 or Older?</h3>
                   <button className='yes' onClick={this.yesClickHandler}>YES</button>
                   <button className='no' onClick={this.noClickHandler}>NO</button>
                </div>  
            );
        }
    } 
};

export default Modal;
