import React, { Component } from 'react';
import './Modal.css';


const modal = (props) => {
    
    if(!this.props.showModal) {
        return null
    }
    
    if(this.props.showModal && this.props.modalErrorFlag) {
        return (
           <div className='showModal'>
               <h6 className='errorMessage'>{this.props.modalErrorMsg}</h6> 
               <h3>Are You 21 or Older?</h3>
               <button className='yes' onClick={this.props.yesClickHandler}>YES</button>
               <button className='no' onClick={props.noClickHandler}>NO</button>
            </div>  
        );
    }

    if(this.props.showModal) {
        return (
           <div className='showModal'>
               <h3>Are You 21 or Older?</h3>
               <button className='yes' onClick={this.yesClickHandler}>YES</button>
               <button className='no' onClick={this.noClickHandler}>NO</button>
            </div>  
        );
    }
}; 


export default modal;
