import React, { Component } from 'react';


class Modal extends Component {
    state= {
        show: false
    };

    /*componentWillMount() {
        this.setState({
            show: true
        });
    } */

    componentDidMount() {
        setTimeout(() => this.setState({ show: true}))
    }

    render() {
        return (
            <div className='modal'>
                <h3>test modal</h3>
            </div>
        );
    };
}

export default Modal;
