import React, { Component } from 'react';
import './Modal.css';


class Modal extends Component {
    
    state= {
        show: false
    };

    /*componentWillMount() {
        this.setState({
            show: true
        });
    } */

    componentDidMount() {
        setTimeout(() => this.setState({ show: true }), 5000);
            return (
                <div className='modal'>
                   <h3>Test Modal</h3>
                </div>
            )
        
    }

    render() {
       if(!this.state.show) {
            return null
        }
    };
};

export default Modal;
