import React, { Component } from 'react';
import './Modal.css';


class Modal extends Component {
    
    state= {
        show: false
    };

    /*componentWillMount() {
        this.setState({
            show: true
        });
    } */

    componentDidMount() {
        this.showModal = setTimeout(() => this.setState({ show: true }), 5000);
    }

    render() {
       if(!this.state.show) {
            return null
        }
    };
};

export default Modal;
