import React, { Component } from 'react';
import './Modal.css';


class Modal extends Component {
    constructor(props) {
        super(props);
    }
    
   /* state= {
        show: false
    }; */

    componentDidMount() {
        setTimeout(() => (this.props.show), 5000);
    };

    render() {

       return (
            <div className='modal'>
                <h3>Test Modal</h3>
            </div>
        );
    };

    
}

export default Modal;
