import React, { Component } from 'react';
import './Modal.css';


class Modal extends Component {
    
    state= {
        show: false
    };

    /*componentWillMount() {
        this.setState({
            show: true
        });
    } */

    componentDidMount() {
        this.showModal = setTimeout(() => this.setState({ show: true }), 5000);
    }

    render() {
       return (
           <div className='showModal'>
               
               <h3>Test Modal</h3>
           </div>  
       )
    };
};

export default Modal;
